DROP TABLE IF EXISTS `{pre}admin`;
CREATE TABLE `{pre}admin` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` longtext NOT NULL,
  `realname` longtext NOT NULL,
  `role_id` int(11) NOT NULL,
  `password` varchar(32) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `count` int(10) unsigned NOT NULL DEFAULT '0',
  `last_login_ip` varchar(16) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `issystem` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}area`;
CREATE TABLE `{pre}area` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `surname` varchar(10) NOT NULL DEFAULT '',
  `pid` int(11) NOT NULL,
  `name` varchar(10) NOT NULL,
  `domain` varchar(50) NOT NULL,
  `default` tinyint(1) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `pctheme` varchar(20) NOT NULL,
  `mobiletheme` varchar(20) NOT NULL,
  `htmlfolder` varchar(20) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}auth_group`;
CREATE TABLE `{pre}auth_group` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL COMMENT '权限分组',
  `description` varchar(30) NOT NULL COMMENT '分组描述',
  `create_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}auth_rule`;
CREATE TABLE `{pre}auth_rule` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(20) NOT NULL COMMENT '规则名',
  `pid` int(11) NOT NULL COMMENT '父ID',
  `sorting` int(11) NOT NULL,
  `controller` varchar(20) NOT NULL COMMENT '控制器名',
  `action` varchar(20) NOT NULL COMMENT 'action',
  `param` varchar(30) NOT NULL COMMENT '参数',
  `create_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_user` int(11) NOT NULL,
  `update_time` timestamp NOT NULL,
  `status` tinyint(1) NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `icon` varchar(20) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=159 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}company`;
CREATE TABLE `{pre}company` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) DEFAULT NULL,
  `name` varchar(60) DEFAULT NULL,
  `address` varchar(60) DEFAULT NULL,
  `postcode` varchar(10) DEFAULT NULL,
  `contact` varchar(30) DEFAULT NULL,
  `mobile` varchar(20) DEFAULT NULL,
  `phone` varchar(30) DEFAULT NULL,
  `fax` varchar(30) DEFAULT NULL,
  `email` varchar(30) DEFAULT NULL,
  `qq` varchar(30) DEFAULT NULL,
  `license` varchar(30) DEFAULT NULL,
  `other` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}config`;
CREATE TABLE `{pre}config` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) DEFAULT NULL,
  `value` varchar(60) DEFAULT NULL,
  `type` int(2) DEFAULT NULL,
  `sorting` bigint(20) DEFAULT NULL,
  `description` text,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=43 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}content`;
CREATE TABLE `{pre}content` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) NOT NULL,
  `sorts_id` int(11) NOT NULL,
  `subsort_id` int(11) NOT NULL,
  `title` varchar(60) NOT NULL,
  `color` varchar(10) NOT NULL,
  `subtitle` varchar(60) NOT NULL,
  `filename` longtext NOT NULL,
  `author` varchar(20) NOT NULL,
  `source` varchar(20) NOT NULL,
  `link` varchar(100) NOT NULL,
  `date` timestamp NOT NULL,
  `pic` varchar(60) NOT NULL,
  `pics` text NOT NULL,
  `content` longtext NOT NULL,
  `tags` text NOT NULL,
  `keywords` varchar(100) NOT NULL,
  `description` varchar(200) NOT NULL,
  `sorting` bigint(20) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `top` tinyint(1) NOT NULL,
  `recommend` varchar(10) NOT NULL DEFAULT '',
  `headline` varchar(10) CHARACTER SET utf8mb4 NOT NULL DEFAULT '',
  `visit` int(10) NOT NULL,
  `like` int(10) NOT NULL,
  `oppose` int(10) NOT NULL,
  `create_user` int(10) NOT NULL,
  `update_user` int(10) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `otherfile` varchar(100) DEFAULT NULL,
  `pictitles` text COMMENT '图片对应标题 逗号分隔',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_content_sorting` (`sorting`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=138 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}content_ext`;
CREATE TABLE `{pre}content_ext` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `content_id` bigint(20) NOT NULL COMMENT '关联content表',
  `modelfield_id` int(11) NOT NULL COMMENT '模型扩展字段ID',
  `value` varchar(255) NOT NULL DEFAULT '' COMMENT '模型扩展字段值 多个值以逗号隔开',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_content_ext_contentid` (`content_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}form`;
CREATE TABLE `{pre}form` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) DEFAULT NULL,
  `table_name` varchar(30) DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `status` tinyint(1) NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `issystem` tinyint(1) DEFAULT NULL,
  `type` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}form_comment`;
CREATE TABLE `{pre}form_comment` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content_id` int(11) DEFAULT NULL COMMENT '内容ID',
  `content` varchar(255) DEFAULT NULL COMMENT '评论内容',
  `pid` int(11) DEFAULT '0' COMMENT '父ID',
  `create_time` datetime DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `ip` varchar(20) DEFAULT NULL,
  `icon` varchar(30) DEFAULT NULL COMMENT '头像',
  `user_id` int(11) DEFAULT NULL COMMENT '用户ID',
  `nickname` varchar(30) DEFAULT NULL COMMENT '昵称',
  `status` tinyint(1) DEFAULT NULL COMMENT '0：前台不显示；1：前台展示；3：审核中  与form表type关联',
  `deleted` tinyint(1) DEFAULT '0',
  `area_id` int(11) DEFAULT NULL COMMENT '区域ID',
  `checked` int(1) DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}form_field`;
CREATE TABLE `{pre}form_field` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `form_id` int(11) DEFAULT NULL,
  `name` longtext,
  `length` bigint(20) DEFAULT NULL,
  `required` tinyint(1) DEFAULT NULL,
  `description` longtext,
  `sorting` bigint(20) DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `deleted` tinyint(1) unsigned zerofill DEFAULT '0',
  `type` varchar(20) DEFAULT NULL COMMENT 'mysql字段类型',
  `issystem` tinyint(1) unsigned zerofill DEFAULT '0' COMMENT '1:系统内置',
  `display` tinyint(1) unsigned zerofill DEFAULT '1' COMMENT '是否显示在后台列表',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_form_field_sorting` (`sorting`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}form_message`;
CREATE TABLE `{pre}form_message` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) DEFAULT NULL,
  `username` varchar(20) DEFAULT NULL,
  `mobile` varchar(20) DEFAULT NULL,
  `content` text,
  `ip` varchar(20) DEFAULT NULL,
  `status` tinyint(1) DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `deleted` tinyint(1) DEFAULT '0',
  `pid` int(11) DEFAULT NULL,
  `icon` varchar(100) DEFAULT NULL,
  `checked` int(1) DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}form_xiaoyao`;
CREATE TABLE `{pre}form_xiaoyao` (
  `id` int(9) NOT NULL AUTO_INCREMENT,
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `ip` varchar(20) DEFAULT NULL,
  `create_user` int(10) DEFAULT NULL,
  `update_user` int(10) DEFAULT NULL,
  `a` int(20) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS `{pre}form_zhaotest`;
CREATE TABLE `{pre}form_zhaotest` (
  `id` int(9) NOT NULL AUTO_INCREMENT,
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `ip` varchar(20) DEFAULT NULL,
  `create_user` int(10) DEFAULT NULL,
  `update_user` int(10) DEFAULT NULL,
  `status` int(10) DEFAULT NULL,
  `area_id` int(10) DEFAULT NULL,
  `deleted` int(1) DEFAULT NULL,
  `name` varchar(20) DEFAULT NULL,
  `sex` int(1) DEFAULT NULL,
  `taste` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}label`;
CREATE TABLE `{pre}label` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(20) DEFAULT NULL,
  `value` text,
  `type` varchar(10) DEFAULT NULL,
  `description` varchar(60) DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `status` tinyint(1) NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `content` text,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=55 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}link`;
CREATE TABLE `{pre}link` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) DEFAULT NULL,
  `group_id` bigint(20) DEFAULT NULL,
  `name` varchar(30) DEFAULT NULL,
  `link` varchar(60) DEFAULT NULL,
  `logo` varchar(60) DEFAULT NULL,
  `sorting` int(11) DEFAULT NULL,
  `status` tinyint(1) NOT NULL,
  `deleted` tinyint(4) NOT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_link_sorting` (`sorting`) USING BTREE,
  KEY `ay_link_gid` (`group_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}link_group`;
CREATE TABLE `{pre}link_group` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL,
  `area_id` int(11) NOT NULL,
  `create_user` int(11) NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `sorting` int(3) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}logs`;
CREATE TABLE `{pre}logs` (
  `id` bigint(20) NOT NULL,
  `level` longtext,
  `event` longtext,
  `ip` longtext,
  `os` longtext,
  `browser` longtext,
  `create_user` longtext,
  `create_time` longtext,
  `status` tinyint(4) NOT NULL,
  `deleted` varchar(4) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}menu`;
CREATE TABLE `{pre}menu` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `pid` int(11) NOT NULL,
  `name` varchar(30) NOT NULL,
  `area_id` int(11) NOT NULL,
  `controller` varchar(30) NOT NULL COMMENT '控制器',
  `action` varchar(30) NOT NULL COMMENT 'action',
  `params` varchar(60) NOT NULL COMMENT '参数',
  `url` varchar(60) NOT NULL,
  `roles_id` json NOT NULL COMMENT '角色IDS，绑定角色',
  `sorting` int(3) NOT NULL,
  `status` int(1) NOT NULL,
  `shortcut` varchar(15) NOT NULL,
  `icon` varchar(20) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_menu_sorting` (`sorting`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=85 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}model`;
CREATE TABLE `{pre}model` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL,
  `type` tinyint(1) NOT NULL,
  `urlname` varchar(30) NOT NULL,
  `listtpl` varchar(30) NOT NULL,
  `contenttpl` varchar(30) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `create_user` int(10) NOT NULL,
  `update_user` int(10) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `issystem` tinyint(1) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}modelfield`;
CREATE TABLE `{pre}modelfield` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `model_id` int(11) NOT NULL,
  `name` varchar(20) NOT NULL,
  `type` varchar(20) NOT NULL,
  `value` text NOT NULL,
  `description` varchar(50) NOT NULL,
  `sorting` bigint(20) NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `status` int(11) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=11 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}role`;
CREATE TABLE `{pre}role` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `areas_id` text NOT NULL,
  `name` longtext NOT NULL,
  `description` varchar(100) NOT NULL,
  `rules_id` text NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `status` tinyint(1) NOT NULL COMMENT '角色状态',
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}site`;
CREATE TABLE `{pre}site` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) DEFAULT NULL,
  `title` varchar(100) DEFAULT NULL,
  `subtitle` varchar(100) DEFAULT NULL,
  `domain` varchar(100) DEFAULT NULL,
  `logo` varchar(60) DEFAULT NULL,
  `keywords` varchar(300) DEFAULT NULL,
  `description` varchar(300) DEFAULT NULL,
  `icp` varchar(50) DEFAULT NULL,
  `pctheme` varchar(100) DEFAULT NULL,
  `mobiletheme` varchar(100) NOT NULL,
  `statistics` varchar(500) DEFAULT NULL,
  `copyright` varchar(500) DEFAULT NULL,
  `qrcode` varchar(100) DEFAULT NULL,
  `service_code` varchar(100) DEFAULT NULL,
  `subscribe_code` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}slide`;
CREATE TABLE `{pre}slide` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `group_id` bigint(20) DEFAULT NULL,
  `pic` longtext,
  `link` varchar(100) DEFAULT NULL,
  `name` varchar(80) DEFAULT NULL,
  `description` varchar(100) DEFAULT NULL,
  `sorting` bigint(20) DEFAULT NULL,
  `create_user` int(11) DEFAULT NULL,
  `update_user` int(11) DEFAULT NULL,
  `create_time` timestamp NULL DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `deleted` tinyint(1) DEFAULT NULL,
  `bgcolor` varchar(30) DEFAULT NULL,
  `bgpic` varchar(100) DEFAULT NULL,
  `status` int(1) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_slide_gid` (`group_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}slide_group`;
CREATE TABLE `{pre}slide_group` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(30) NOT NULL,
  `area_id` int(11) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp NOT NULL,
  `update_time` timestamp NOT NULL,
  `deleted` tinyint(1) NOT NULL,
  `sorting` int(3) NOT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE KEY `id` (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}sorts`;
CREATE TABLE `{pre}sorts` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(3) DEFAULT NULL,
  `model_id` int(5) DEFAULT NULL,
  `pid` int(10) DEFAULT NULL,
  `name` varchar(30) DEFAULT NULL,
  `listtpl` varchar(50) DEFAULT NULL,
  `contenttpl` varchar(50) DEFAULT NULL,
  `status` tinyint(1) DEFAULT NULL,
  `outlink` varchar(100) DEFAULT NULL,
  `subname` longtext,
  `pics` json DEFAULT NULL,
  `ico` longtext,
  `pic` longtext,
  `title` varchar(100) DEFAULT NULL,
  `keywords` varchar(100) DEFAULT NULL,
  `description` varchar(200) DEFAULT NULL,
  `urlname` varchar(20) DEFAULT NULL,
  `sorting` bigint(20) DEFAULT NULL,
  `create_user` varchar(10) DEFAULT NULL,
  `update_user` longtext,
  `create_time` longtext,
  `update_time` longtext,
  `deleted` tinyint(1) NOT NULL,
  `enname` varchar(20) DEFAULT NULL,
  `slogan` varchar(255) DEFAULT NULL,
  `content` text COMMENT '专题页面内容',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `ay_content_sort_sorting` (`sorting`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=26 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}statistics`;
CREATE TABLE `{pre}statistics` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `start_time` int(12) DEFAULT NULL,
  `ip` varchar(16) DEFAULT NULL,
  `os` varchar(20) DEFAULT NULL,
  `content_id` int(11) DEFAULT NULL COMMENT '0:首页',
  `sort_id` int(11) DEFAULT NULL COMMENT '0:首页',
  `from` varchar(20) DEFAULT NULL,
  `end_time` datetime DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS `{pre}sysup`;
CREATE TABLE `{pre}sysup` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `version_num` varchar(255) DEFAULT NULL,
  `version_path` varchar(255) DEFAULT NULL COMMENT '是否强更',
  `prev_version_num` varchar(255) DEFAULT NULL,
  `prev_version_path` varchar(255) DEFAULT NULL,
  `zip_file` varchar(255) DEFAULT NULL COMMENT '更新包',
  `status` varchar(255) DEFAULT '0' COMMENT '是否开启更新',
  `deleted` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;


DROP TABLE IF EXISTS `{pre}tags`;
CREATE TABLE `{pre}tags` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` tinyint(1) DEFAULT NULL COMMENT '绑定区域ID',
  `name` varchar(30) DEFAULT NULL COMMENT '区域名',
  `link` varchar(60) DEFAULT NULL COMMENT '链接',
  `status` tinyint(1) NOT NULL COMMENT '状态:0:不可用；1：可用',
  `deleted` tinyint(1) NOT NULL COMMENT '删除 0：未删除；1：已删除； 删除操作不进行物理删除',
  `create_user` int(11) DEFAULT NULL COMMENT '创建者ID',
  `update_user` int(11) DEFAULT NULL COMMENT '更新者ID',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `{pre}test`;
CREATE TABLE `{pre}test` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `a` varchar(255) DEFAULT NULL,
  `b` varchar(255) DEFAULT NULL,
  `c` varchar(255) DEFAULT NULL,
  `d` varchar(255) DEFAULT NULL,
  `e` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

DROP TABLE IF EXISTS `{pre}test2`;
CREATE TABLE `{pre}test2` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `aa` varchar(255) DEFAULT NULL,
