-- ----------------------------
-- Table structure for jelly_admin
-- ----------------------------
DROP TABLE IF EXISTS `{pre}admin`;
CREATE TABLE `{pre}admin`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `openid` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `name` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '管理员名',
  `realname` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '真实姓名',
  `role_id` int(11) NOT NULL COMMENT '角色ID',
  `password` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '密码',
  `status` tinyint(1) NOT NULL COMMENT '状态',
  `count` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '登录次数',
  `last_login_ip` varchar(16) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '上一次登录ID',
  `create_user` int(11) NOT NULL COMMENT '创建者',
  `update_user` int(11) NOT NULL COMMENT '更新者',
  `create_time` timestamp(0) NOT NULL COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL COMMENT '更新时间',
  `deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '删除',
  `issystem` tinyint(1) NOT NULL DEFAULT 0 COMMENT '系统',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Table structure for jelly_area
-- ----------------------------
DROP TABLE IF EXISTS `{pre}area`;
CREATE TABLE `{pre}area`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `surname` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '中文名',
  `pid` int(11) NOT NULL,
  `name` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '区域英文名称',
  `domain` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '绑定域名',
  `default` tinyint(1) NOT NULL COMMENT '是否是默认区域',
  `create_user` int(11) NOT NULL COMMENT '创建者',
  `update_user` int(11) NOT NULL COMMENT '更新者',
  `create_time` timestamp(0) NOT NULL COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL COMMENT '更新时间',
  `deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '删除',
  `pctheme` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'PC模板',
  `mobiletheme` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '移动端模板',
  `htmlfolder` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '模板保护',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

