-- ----------------------------
-- Table structure for jelly_content
-- ----------------------------
DROP TABLE IF EXISTS `{pre}content`;
CREATE TABLE `{pre}content`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题',
  `area_id` int(11) NOT NULL COMMENT '区域ID',
  `sorts_id` int(11) NOT NULL COMMENT '分类ID',
  `subsort_id` int(11) NOT NULL COMMENT '子分类ID',
  `color` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标题颜色',
  `subtitle` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '子标题',
  `filename` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '附件',
  `author` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '作者',
  `source` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '来源',
  `link` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '外部链接',
  `date` timestamp(0) NOT NULL COMMENT '定时发布时间',
  `pic` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '缩略图',
  `pics` text CHARACTER SET utf16 COLLATE utf16_general_ci NOT NULL COMMENT '多图',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '内容',
  `tags` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '标签',
  `keywords` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '关键词',
  `description` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '描述',
  `sorting` bigint(20) NOT NULL COMMENT '排序',
  `top` tinyint(1) NOT NULL COMMENT '置顶',
  `recommend` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '推荐',
  `headline` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '' COMMENT '头条',
  `visit` int(10) NOT NULL COMMENT '访问量',
  `like` int(10) NOT NULL COMMENT '点赞量',
  `oppose` int(10) NOT NULL COMMENT '反对量',
  `create_user` int(10) NOT NULL COMMENT '创建者',
  `update_user` int(10) NOT NULL COMMENT '更新者',
  `create_time` timestamp(0) NOT NULL COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL COMMENT '更新时间',
  `otherfile` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '其它附件',
  `pictitles` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '图片对应标题 逗号分隔',
  `status` tinyint(1) NOT NULL COMMENT '状态',
  `deleted` tinyint(1) NOT NULL DEFAULT 0 COMMENT '删除',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 24 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of jelly_content
-- ----------------------------
INSERT INTO `{pre}content` VALUES (1, 'JellyCMS3.8.1正式发布', 1, 3, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200819/1597821934_3be768770bb62d65c19e.jpg', '', '<p>JellyCMS3.8.1正式发布</p>', '', '', '', 256, 1, '1', '1', 15, 0, 0, 1, 1, '2020-08-18 20:04:01', '2020-08-19 15:25:36', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (2, '新手如何搭建自己的网站?', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200819/1597821934_3be768770bb62d65c19e.jpg', '', '<p style=\"margin-top: 26px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第一步：确定网站类型</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">首先确定网站类型，不同的网站类型选择的开源程序一般不同，如企业网站推荐使用JellyCMS，功能全面，seo功能完善。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第二步：购买域名、服务器或主机</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">买东西大家一定会购买，就算不会，你在哪个平台买可以直接咨询相应平台的客服就可以，这里就不多说了，只是有两点需要注意一下：</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">1、如果网站不准备备案，一定要买香港或者海外的主机，国内主机不备案网站无法上线；</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">2、一般中小型企业选择主机就够用了，而且价格相对便宜一点，当然如果您是土豪也可以直接购买服务器。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第三步：选择合适的开源程序</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">常见的开源程序有JellyCMS、帝国，织梦，wordpress，phpcms，discuz等等，大家按需要选择合适的就行。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">如果想仿站JellyCMS就比较适合；</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">如果想做资讯类站点，织梦就比较方便，搭建快，后台支持采集功能，缺点就是不安全，我见过很多企业因为资讯选择织梦程序导致服务器被入侵；</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">如果想做响应式网站，选择JellyCMS比较合适，功能完善，其他开源程序很少支持响应式的；</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">如果想做博客，可以用wordpress，比较适合做博客网站；</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">大家可以根据自己的需求选择就好，这里就不再一一举例了。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第四步：上传程序源码到空间</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">大家找到需要的开源程序后去相应的网站下载安装文件，然后利用ftp上传到空间根目录。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第五步：安装网站</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">安装网站每个开源程序基本都差不多，一般是会要填数据库名与密码，设置管理员账号密码，大多数直接下一步，下一步，按提示填写信息就可以了。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第六步：后台设置</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">后台设置一般包括，网站名称，关键词，图片修改，栏目搭建等。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第七步：内容填充</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">内容填充包括网站简介，产品介绍，联系方式，资讯等，这里需要注意的是，建议一定要用文字或文字+图片来填充，很多网站为了方便，公司简介就是一张图片来介绍，文字全在图片上，甚至有些连产品介绍也是直接全部用图片，这样非常不利于搜索引擎的爬取与收录。</span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-strong\" style=\"font-size: 18px; font-weight: 700;\">第八步：搭建完成</span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\"><span class=\"bjh-br\"></span></span></p><p style=\"margin-top: 22px; margin-bottom: 0px; padding: 0px; line-height: 24px; color: rgb(51, 51, 51); text-align: justify; font-family: arial; white-space: normal; background-color: rgb(255, 255, 255);\"><span class=\"bjh-p\">网站搭建完成后还没有结束，重要的是如何去运营网站，我们也会不定期的分享网站运营方面的知识，大家可以持续关注。</span></p><p><br/></p>', '', '新手如何搭建自己的网站', '首先确定网站类型，不同的网站类型选择的开源程序一般不同，如企业网站推荐使用米拓，功能全面，seo功能完善。', 255, 0, '', '', 13, 0, 0, 1, 1, '2020-08-21 09:24:13', '2020-09-04 12:16:42', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (3, '新手如何选域名和购买域名?', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<h1 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 28px; color: rgb(79, 79, 79); line-height: 36px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">前言</h1><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">本文介绍的是如何购买一个网络域名，是建立个人网站的基础准备之一。</p><h1 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 28px; color: rgb(79, 79, 79); line-height: 36px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">知识储备</h1><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>域名是什么？</p></li><li><p>域名系统是什么？</p></li><li><p>HTTP 是什么？</p></li><li><p>HTTPS 是什么？</p></li></ul><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">域名是什么？</h2><blockquote style=\"box-sizing: border-box; outline: 0px; margin: 0px 0px 24px; padding: 16px; color: rgba(0, 0, 0, 0.5); border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 14px; font-variant-ligatures: common-ligatures; white-space: normal; word-break: break-word !important;\"><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; color: rgb(153, 153, 153); line-height: 22px; overflow-wrap: break-word;\">网域名称（英语：Domain Name，简称：Domain），简称域名、网域，是由一串用点分隔的字符组成的互联网上某一台计算机或计算机组的名称，用于在数据传输时标识计算机的电子方位。域名可以说是一个 IP 地址的代称，目的是为了便于记忆后者。例如，wikipedia.org&nbsp;是一个域名，和 IP 地址 208.80.152.2 相对应。人们可以直接访问&nbsp;wikipedia.org&nbsp;来代替 IP 地址，然后域名系统（DNS）就会将它转化成便于机器识别的 IP 地址。这样，人们只需要记忆&nbsp;wikipedia.org&nbsp;这一串带有特殊含义的字符，而不需要记忆没有含义的数字。</p></blockquote><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">域名系统是什么？</h2><blockquote style=\"box-sizing: border-box; outline: 0px; margin: 0px 0px 24px; padding: 16px; color: rgba(0, 0, 0, 0.5); border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 14px; font-variant-ligatures: common-ligatures; white-space: normal; word-break: break-word !important;\"><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; color: rgb(153, 153, 153); line-height: 22px; overflow-wrap: break-word;\">域名系统（英文：Domain Name System，缩写：DNS）是互联网的一项服务。它作为将域名和IP地址相互映射的一个分布式数据库，能够使人更方便地访问互联网。DNS 使用 TCP 和 UDP 端口 53。当前，对于每一级域名长度的限制是63个字符，域名总长度则不能超过 253 个字符。</p></blockquote><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">HTTP 是什么？</h2><blockquote style=\"box-sizing: border-box; outline: 0px; margin: 0px 0px 24px; padding: 16px; color: rgba(0, 0, 0, 0.5); border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 14px; font-variant-ligatures: common-ligatures; white-space: normal; word-break: break-word !important;\"><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; color: rgb(153, 153, 153); line-height: 22px; overflow-wrap: break-word;\">超文本传输协议（英语：HyperText Transfer Protocol，缩写：HTTP）是一种用于分布式、协作式和超媒体信息系统的应用层协议。HTTP是万维网的数据通信的基础。</p></blockquote><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">HTTPS 是什么？</h2><blockquote style=\"box-sizing: border-box; outline: 0px; margin: 0px 0px 24px; padding: 16px; color: rgba(0, 0, 0, 0.5); border-left: 8px solid rgb(221, 223, 228); background: rgb(238, 240, 244); overflow: auto; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif; font-size: 14px; font-variant-ligatures: common-ligatures; white-space: normal; word-break: break-word !important;\"><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; color: rgb(153, 153, 153); line-height: 22px; overflow-wrap: break-word;\">超文本传输安全协议（英语：Hypertext Transfer Protocol Secure，缩写：HTTPS，常称为HTTP over TLS，HTTP over SSL 或 HTTP Secure）是一种通过计算机网络进行安全通信的传输协议。HTTPS 经由 HTTP 进行通信，但利用 SSL/TLS 来加密数据包。HTTPS 开发的主要目的，是提供对网站服务器的身份认证，保护交换数据的隐私与完整性。这个协议由网景公司（Netscape）在1994 年首次提出，随后扩展到互联网上。</p></blockquote><h1 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 28px; color: rgb(79, 79, 79); line-height: 36px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">实现方法</h1><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">学习了知识储备中的技术点，我们可以看出，为了搭建一个完整的网站，除了提供服务的服务器之外，购买一个域名是必不可少的，另外，配置 https 也是必不可少的一步，因此我们额外购买 SSL 证书。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>如何购买一个域名，分为以下几个步骤：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>选择域名服务提供商</p></li><li><p>购买域名</p></li><li><p>购买 SSL 证书</p></li></ol><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">选择域名服务提供商</h2><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">域名服务提供商有很多，无论是什么原因导致你最终选择哪一家，我都是以最便捷易懂的目的来推荐。以下是我推荐的几个域名服务提供商：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>阿里云（万网）&nbsp;https://wanwang.aliyun.com/domain/</p></li><li><p>腾讯云&nbsp;https://dnspod.cloud.tencent.com/</p></li><li><p>Godaddy&nbsp;https://sg.godaddy.com/zh/domains</p></li></ol><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">购买域名</h2><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">JellyCMS.cn 是在阿里云（万网）上购买的，因此本文以阿里云（万网）为例，介绍如何购买一个域名。</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>在浏览器打开阿里云（万网）链接&nbsp;https://wanwang.aliyun.com/domain/，进入万网主页。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br/></p></li><li><p>在输入框输入自己想要给网站起的名字（最好是纯英文数字，当然中文现在也支持了，但为了走向世界，还是老老实实用英文数字作为你的网站域名吧），点击查域名按钮。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>我输入的是 iloveyou，搜索结果出来了，因为这个 iloveyou 必定火爆，所以搜索结果显示大致分为两种，未注册和已注册，你可以看到已注册的显示价格都非常高，那是因为已经被别人购买，道理如同商标抢注，所以你也可以用这个方法来赚钱，未注册的相对便宜，你可以直接点击加入清单，稍后购买，也可以鼠标悬停在更多价格按钮上参考1年、3年、5年、10年期的价格，一次购买的期限越长，相对平均价格越低，如果只购买1年期，到期如果需要保留并才续费，那价格肯定会比直接买多年期要高，所以购买前一定要想好计划好。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>为了说明一般情况，我又换了一个名字 <span style=\"color: rgb(77, 77, 77); font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; font-variant-ligatures: common-ligatures; background-color: rgb(255, 255, 255);\">JellyCMS</span>搜索。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>这次可以看到，由于是个性化起名，显示的价格一下就降低了很多，而且选择余地也很多，可以根据自己的实际情况进行购买。我选择了 top 后缀的域名，点击加入清单按钮，接着点击立即结算按钮，进入结算页面。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>付款后，你就拥有了一个正式的域名了，这时你就可以点击&nbsp;https://dc.console.aliyun.com&nbsp;进入域名控制台进行各种配置操作了。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br/></p></li></ol><h2 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 24px; color: rgb(79, 79, 79); line-height: 32px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">购买 SSL 证书</h2><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">在阿里云（万网）购买了域名服务以后，就可以接着购买 SSL 证书了，点击域名控制台相应域名列表后的管理链接，进行具体的配置管理，包括购买 SSL 证书。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>可以看到【免费开启SSL证书】这个链接，点击后发现有收费的和免费的两个选择，我认为免费的对于个人网站够用，要求不高的情况下可以选择它。<br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/><br style=\"box-sizing: border-box; outline: 0px; margin: 0px; padding: 0px; overflow-wrap: break-word;\"/>填写要申请的域名后，就可以一路免费（或者收费）直到拥有 SSL 证书了。</p><h1 style=\"box-sizing: border-box; outline: 0px; margin: 8px 0px 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 28px; color: rgb(79, 79, 79); line-height: 36px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">总结</h1><p style=\"box-sizing: border-box; outline: 0px; margin-top: 0px; margin-bottom: 16px; padding: 0px; font-family: &quot;Microsoft YaHei&quot;, &quot;SF Pro Display&quot;, Roboto, Noto, Arial, &quot;PingFang SC&quot;, sans-serif; font-size: 18px; color: rgb(77, 77, 77); line-height: 26px; overflow-wrap: break-word; font-variant-ligatures: common-ligatures; white-space: normal; background-color: rgb(255, 255, 255);\">本文简单介绍了购买域名以及 SSL 证书的步骤，只是完成了搭建个人网站的一小部分工作，接下来还会继续写关于搭建个人网站的其他必要工作，包括域名的绑定，SSL 证书的设置方法。</p><p><br/></p>', '', '新手如何选域名,新手如何购买域名', '本文介绍的是如何购买一个网络域名，是建立个人网站的基础准备之一', 255, 0, '', '', 33, 0, 0, 1, 1, '2020-08-21 09:30:20', '2020-09-04 10:27:55', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (4, 'JellyCMS', 1, 5, 5, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200821/1597979080_56aa87415a0ccad45be8.jpg', '', '<p><span style=\"color: rgb(88, 96, 105); font-family: -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">JellyCMS是全新内核且永久开源免费的PHP企业网站开发建设管理系统，是一套高效、简洁的可免费商用的PHP CMS源码，能够满足各类企业网站开发建设的需要。只要懂HTML就可快速开发企业网站。官方提供了大量网站模板免费下载和使用，将致力于为广大开发者和企业提供最佳的网站开发建设解决方案。</span></p>', '', 'JellyCMS', 'JellyCMS是全新内核且永久开源免费的PHP企业网站开发建设管理系统，是一套高效、简洁的可免费商用的PHP CMS源码，能够满足各类企业网站开发建设的需要。只要懂HTML就可快速开发企业网站。官方提供了大量网站模板免费下载和使用，将致力于为广大开发者和企业提供最佳的网站开发建设解决方案。', 0, 0, '', '', 2, 0, 0, 1, 1, '2020-08-21 11:04:09', '2020-08-21 11:05:06', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (5, 'JellyCRM', 1, 5, 5, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200821/1597979175_7b0ff2fcfcd38651743c.jpg', '', '<p>JellyCRM开源客户关系管理系统</p>', '', 'JellyCRM开源客户关系管理系统', 'JellyCRM开源客户关系管理系统', 0, 0, '', '', 7, 0, 0, 1, 1, '2020-08-21 11:06:21', '2020-09-01 11:23:57', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (6, 'JellySHOP', 1, 5, 5, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200821/1597979496_5cd9c6081669271c2af5.jpg', '', '<p>JellySHOP开源商城</p>', '', 'JellySHOP开源商城', 'JellySHOP开源商城', 0, 0, '', '', 28, 0, 0, 1, 1, '2020-08-21 11:08:33', '2020-09-01 11:24:26', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (7, 'JellyTOOLS', 1, 5, 5, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200821/1597979518_47198bb10af0cad74efc.jpg', '/static/upload/20200821/1597979518_47198bb10af0cad74efc.jpg,/static/upload/20200821/1597979518_47198bb10af0cad74efc.jpg', '<p>JellyTOOL</p>', '', 'JellyTOOLS', 'JellyTOOL', 0, 1, '1', '', 47, 0, 0, 1, 1, '2020-08-21 11:10:40', '2020-09-04 10:34:09', '', '123,2', 1, 0);
INSERT INTO `{pre}content` VALUES (8, 'JellyALL', 1, 5, 5, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200821/1597979080_56aa87415a0ccad45be8.jpg', '', '<p><span style=\"color: rgb(88, 96, 105); font-family: -apple-system, BlinkMacSystemFont, &quot;Segoe UI&quot;, Helvetica, Arial, sans-serif, &quot;Apple Color Emoji&quot;, &quot;Segoe UI Emoji&quot;; font-size: 14px; background-color: rgb(255, 255, 255);\">JellyCMS是全新内核且永久开源免费的PHP企业网站开发建设管理系统，是一套高效、简洁的可免费商用的PHP CMS源码，能够满足各类企业网站开发建设的需要。只要懂HTML就可快速开发企业网站。官方提供了大量网站模板免费下载和使用，将致力于为广大开发者和企业提供最佳的网站开发建设解决方案。</span></p>', '', 'JellyCMS', 'JellyCMS是全新内核且永久开源免费的PHP企业网站开发建设管理系统，是一套高效、简洁的可免费商用的PHP CMS源码，能够满足各类企业网站开发建设的需要。只要懂HTML就可快速开发企业网站。官方提供了大量网站模板免费下载和使用，将致力于为广大开发者和企业提供最佳的网站开发建设解决方案。', 0, 0, '', '', 9, 0, 0, 1, 1, '2020-08-21 11:04:09', '2020-08-21 11:05:06', '', '', 0, 0);
INSERT INTO `{pre}content` VALUES (9, 'SEO搜索引擎优化的基础知识', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">加入百度站长平台，添加百度统计工具。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">1.服务器日志。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2.Sitemap:&nbsp;Sitemap制作。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">3.robots</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">4.hl、h2标签，使用黑体。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">5.死链接。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">6.死循环。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">7.首页www和不带www,解析，301重定向。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">8.www与/index.html,删除页面或301重定向。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">9.nofollow 使用。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">10.URL字母一律小写。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">11.tag页面的URL中文化或拼音。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">12.URL伪静态（如果收录好，则无须修改）。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">13.绝对路径与相对路径。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">14.目录形式与文件形式，URL加/。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">15.网页层级：小站3层以内，大站不限，逻辑清晰。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">16.新站：URL的层级、命名规范化。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">17.改版：原URL不变，做301到新站。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">18.面包屑路径使用。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">19.图片导航/文字导航：导航栏必须含有关键词。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">20.入口：非常重要，增加核心词/列表的入口，除了在首页增加文章入口，在文章页通过锚文本增加入口，通过丰富的内容，达到收录增加的目的。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">21.首页：导出外链不超过100条，尽可能使二三级热词出现在首页，也是入口问题。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">22.列表页：增加两三段原创文字描述。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">23.列表翻页：如果多，做成两排，也是增加入口。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">24.文章页相关调用：必须调用相关文章，每篇文章调用10-20篇，按关键词调用，但必须随机，目的是增加页面关键词密度，同时去噪化。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">25.相关产品链接：增加入口。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">26.文章页尾部：相关推荐，每次推荐几篇，也是随机。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">27.蜘蛛陷阱：flash、图片、js调用、session ID.各种跳转、frame框架、必须登录、导航栏js、必须使用cookies。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">28.站点尺寸：如果网站关键词过多，尽量建立多个列表，让网站貌似非常强大。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">29.二级域名：小站不使用，大站或人手多必须用，对排名有优势。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">30.html正文位置：最好在前100行出现。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">31.alt属性描述。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">32.LOGO的alt描述，第一次很重要。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">33.浏览器的兼容性。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">34.代码优化：冗余代码去掉，如对css的颜色、字体、大小重新定位。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">35.表格/嵌套：去掉。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">36.外包文件：html只写一条代码，调用其他的外部文件即可。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">37.css出现在html后面：title等文字在100行以内出现。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">38.网页GZIP压缩。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">39.页面大小优化：100k以内。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">40.W3C/mip标准化</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">41.检查作弊。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">42.检查隐藏页面。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">43.排除密度作弊,大量堆砌关键词。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">44.黑链或被惩罚。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">45.合并过多js调用。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">46.检查域名劫持。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">47.代码漏洞，网站被挂木马。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">48.敏感词过滤。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">49.网站打开速度、美观设计。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">50.UED,考虑产品匹配、推荐等因素。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">51.首页关键词密度：3%~11%。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">52.正文已经删除，但URL还能打开。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">53.要优化的登录页出现在首页左边最前边，不重要的靠后</p><p><br/></p>', '', 'SEO搜索引擎优化的6大关键基础知识', '搜索引擎优化并不是一种迷人的SEO营销形式，因为它往往需要数周乃至数月的时间才能看到一些效果，对于SEO新手来说，等待是整个优化过程最为漫长和可怕的事情，但是，每天花费数分钟专注于采取一些基本步骤来进行搜索引擎优化，你可以提高自身的基础知识，并获得成功。', 0, 0, '', '', 1, 0, 0, 1, 1, '2020-09-04 10:37:22', '2020-09-04 10:37:58', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (10, '搜索引擎蜘蛛爬行以及抓取原理', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛，在搜索引擎系统中又被称之为“蜘蛛”或“机器人”，是用来爬行和访问页面的程序。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">① 爬行原理</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛访问网页的过程，就好比用户使用的浏览器。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛向页面发出访问请求，该页面的服务器则返回该页面的HTML代码。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛将收到的HTML代码存入搜索引擎的原始页面数据库中。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">② 如何爬行</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">为了提高搜索引擎蜘蛛的工作效率，通常采用多个蜘蛛并发分布爬行。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">同时，分布爬行还分为两种模式：深度优先和广度优先。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">深度优先：沿着发现的链接一直爬行，直到没有任何链接。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">广度优先：先这一页面上的所有链接爬行完毕之后，才会沿着第二层页面继续这样爬行。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">③ 蜘蛛必遵守的协议</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛在访问网站之前，都会先访问网站根目录下的robots.txt文件。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎蜘蛛不会去抓取robots.txt文件中禁止爬行的文件或目录。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">④ 常见搜索引擎蜘蛛</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">百度蜘蛛：Baiduspider</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">谷歌蜘蛛：Googlebot</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">360蜘蛛：360Spider</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">SOSO蜘蛛：Sosospider</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">有道蜘蛛：YoudaoBot，YodaoBot</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜狗蜘蛛：Sogou News Spider</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">必应蜘蛛：bingbot</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">Alexa蜘蛛：ia_archiver</p><p><br/></p>', '', '搜索引擎蜘蛛爬行以及抓取原理', '则返回该页面的HTML代码。搜索引擎蜘蛛将收到的HTML代码存入搜索引擎的原始页面数据库中。② 如何爬行为了提高搜索引擎蜘蛛的工作效率，通常采用多个蜘蛛并发分布爬行。同时，分布爬行还分为两种模式：深度优先和广度优先。深度优先：沿着发现的链接一直爬行，直到没有任何链接。广度优先：先这一页面上的所有链接爬行完毕之后，才会沿着第二层页面继续这样爬行。③ 蜘蛛必遵守的协议搜索引擎蜘蛛在访问网站之前，都会先', 0, 0, '', '', 0, 0, 0, 1, 0, '2020-09-04 10:39:16', '0000-00-00 00:00:00', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (11, 'SEO优化内链如何布局提升蜘蛛抓取', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">SEO优化中如何有效的布局内链,让蜘蛛抓取SEO优化，那么网站优化时，我们要如何合理分析内外链呢?</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">1、内页多关键词指向首页</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">现在还有很多网站在内容页搞了好多关键词，但是指向的全都是首页，这个小技巧在前几年还是有些用的，但是现在算是作弊的行为，切记。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">2、是否有相关推荐</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">每个页面是否有推荐跟内容相关的内部指向链接，这个很重要，对用户，对蜘蛛都是非常有帮助的。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">3、每个页面是否能链接到别的相关页面</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">内页要做相关的推荐，还有栏目页、专题页、首页都是一样的，只不过要从不同的定位角度来指向而已。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">那么如何检查外链呢?一般常用两种方法：</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">（1）通过domain指令</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">可以找出链接你的网站是哪些，检查一下是否有和不良的网站出现在一起，如果有要尽快处理掉，不然也是会有影响的。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box;\">（2）</span>通过友情链接</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">查看友情链接是否正常，比如你链接了别人，别人却把你的链接给撤销了，或者别人的网站打不开了等等之类的情况，需要及时的处理。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">4、发外链吸引蜘蛛的方法</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box;\">（1）</span>很多站长发布外链都是带个网站的首页网址就算了，我认为这种优化方式比较单一，如果你的网站权重低，更新不频繁的话，可能蜘蛛通过链接到了你的网站就不再深入去爬行了。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box;\">（2）</span>一般更新完文章，就可以去各大论坛和博客发布文章，然后带上刚刚发布的文章地址。这种效果是比较不错的，小伙伴们可以试试。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">5、和一些更新比较频繁的网站交换链接</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">友链的作用大家也都知道的，友情链接对于网站的排名起到一定的作用，同时友情链接又引导蜘蛛在网站之间来回爬行的一种链接，对于网站的排名和收录都是非常有帮助的，所以我们要经常和一些更新比较频繁的网站交换链接。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">6、网站站内文章之间的链接</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">不论是文章之间还是栏目和网站首页之间，全部要有一个或者几个链接的路径，这个路径是蜘蛛在你网站上面爬行的一种链接，同时也可以让用户点击的链接，对于网站的收录，排名和权重的提高都是比较不错的。</p><p><br/></p>', '', 'SEO优化内链如何布局提升蜘蛛抓取', '年还是有些用的，但是现在算是作弊的行为，切记。2、是否有相关推荐每个页面是否有推荐跟内容相关的内部指向链接，这个很重要，对用户，对蜘蛛都是非常有帮助的。3、每个页面是否能链接到别的相关页面内页要做相关的推荐，还有栏目页、专题页、首页都是一样的，只不过要从不同的定位角度来指向而已。那么如何检查外链呢?一般常用两种方法：（1）通过domain指令可以找出链接你的网站是哪些，检查一下是否有和不良的网站出', 0, 0, '', '', 3, 0, 0, 1, 0, '2020-09-04 10:39:53', '0000-00-00 00:00:00', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (12, '网站图片SEO优化处理', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">总结出了以下六种方法，协助我们优化网站和手机端的图片，以达到优化友好，迅速被录入的作用。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">1、不要盗用图片尽量原创</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">尽量自己做图片，有很多免费的图片素材，我们可以通过拼接，做出我们需要的图片。<span style=\"box-sizing: border-box; text-indent: 2em;\">在平时工作的时候，发现和自己网站相关的图片可以先保存下来，在本地做出分类和标记。</span><span style=\"box-sizing: border-box; text-indent: 2em;\">网站需要图片的时候，看看相关的图片，自己着手做一个图片。这是一个长期积累的过程，随之时间的增加，自己的素材量也会越来越大。熟练了再做图片就得心应手了。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">2、网站图片保存路径</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">这个问题很多站长都没有注意，图片在传到到网站的时候，尽量把图片保存在一个目录下面，<span style=\"box-sizing: border-box; text-indent: 2em;\">或者根据网站栏目做好相应的图片目录，上传的时候路径要相对固定，方便蜘蛛抓取，蜘蛛在访问到这个目录的时候就会“知道”这个目录里面保存的是图片;</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">图片文件命名最好使用一些有规律的或者意义的方法，可以使用时间、栏目名称或者网站名称来命名。<span style=\"box-sizing: border-box; text-indent: 2em;\">其实这样是培养搜索引擎蜘蛛抓取习惯，方便将来更快的识别网站图片内容。让蜘蛛抓的顺心了，网站被收录的几率就增加了，何乐而不为呢!</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">3、图片周围要有相关文字</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站图片是能把信息直接呈现给用户一个方法，搜索引擎在抓取网站内容的时候，也会检测这篇文章是否有配图、视频或者表格等等，<span style=\"box-sizing: border-box; text-indent: 2em;\">这些都是可以增加文章分数值的元素，其他的几个形式暂时不表，这里我们只说说关于图片周围相关文字的介绍。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">首先图片周边文字要与图片本身内容相符，例如你的文章说的是做网站优化，里面配图是一道菜谱的图片，这不是挂羊头卖狗肉么?<span style=\"box-sizing: border-box; text-indent: 2em;\">用户的访问感会极差，搜索引擎通过相关算法识别这张图片以后，也会觉得图文不符，给你差评哦。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">所以，每篇文章最少要配一张相应的图片，而且在图片的周围要出现和你网站标题相关的内容。不但能帮助搜索引擎理解图片，还可以增加文章的可读性、用户体验友好度以及相关性。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">4、图片添加alt、title标签</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">许多站长在添加网站图片时可能没有留意这些细节，有的可能觉得麻烦，希望大家千万别有这种想法，这是大错特错的。<span style=\"box-sizing: border-box; text-indent: 2em;\">搜索引擎抓取网站图片的时候，atl标签是它首先抓取的，也是识别图片内容最重要的核心因素之一，图片的alt属性是直接告诉搜索引擎这是啥网站图片，以及这张要表达什么意思;</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">title标签是用户指向这张图片的时候，会显示的提示内容，这是增加用户体验度和增加网站关键词的一个小技巧。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">alt和title标签</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">还有就是这两个属性，会给有阅读障碍的访问者提供访问的便利，例如：有盲人在访问你网站的时候，他看不到屏幕上的内容，可能是通过读屏软件来阅读的，如果有alt属性，软件会直接读出alt属性里的文字，给他们的访问提供方便。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">5、图片的大小和分辨率</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">尽管两者看起来有些相同，但还是有很大的差别，相同大小的图片，分辨率更高的话，网站最终体积也会越大。这一点大家要搞清楚。<span style=\"box-sizing: border-box; text-indent: 2em;\">网站的上的图片，一直以来都提倡用尽量小的图片，去最大化呈现内容。为什么要这样呢?</span><span style=\"box-sizing: border-box; text-indent: 2em;\">因为小尺寸的图片会加载的更快，不会让访问者等待太久，特别是手机访问的时候，由于手机上网速度和流量的限制，用户更愿意访问能立即打开的页面，小尺寸图片就更有优势了。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">在这里我们尽量做好平衡，在图片不失真的情况下，尺寸最好尽量的小。<span style=\"box-sizing: border-box; text-indent: 2em;\">现在有很多在线给图片瘦身的工具，各位站长可以去尝试一下，把网站的图片适当的压缩一下，一方面可以减少你服务器带宽的压力，另外还能给用户有流畅的体验。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">6、手机端自动适应</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">很多站长都遇到过网站在电脑访问图片显示很正常，可是从手机端就会出现错位等等情况，这就是大尺寸的图片给不同尺寸的终端造成错位和显示不全的情况。<span style=\"box-sizing: border-box; text-indent: 2em;\">其实这个问题很好解决，在添加图片的时候宽高最好不要使用绝对大小，使用百分比就解决了。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">具体说，CSS代码不能指定像素宽度：width:xxx px;只能指定百分比宽度：width: xx%;或者width:auto就可以了。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box; text-indent: 2em;\">这样做的目的也是为了让百度的移动蜘蛛抓取的时候有个良好的体验，这也是为了更符合百度移动落地页体验。</span></p><p><br/></p>', '', '网站图片SEO优化处理', '时候，发现和自己网站相关的图片可以先保存下来，在本地做出分类和标记。网站需要图片的时候，看看相关的图片，自己着手做一个图片。这是一个长期积累的过程，随之时间的增加，自己的素材量也会越来越大。熟练了再做图片就得心应手了。2、网站图片保存路径这个问题很多站长都没有注意，图片在传到到网站的时候，尽量把图片保存在一个目录下面，或者根据网站栏目做好相应的图片目录，上传的时候路径要相对固定，方便蜘蛛抓取，蜘蛛', 0, 0, '1', '', 8, 0, 0, 1, 0, '2020-09-04 10:41:32', '0000-00-00 00:00:00', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (13, '如何提高搜索引擎抓取频率', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">1、网站内容更新</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">搜索引擎抓取内容，只是针对个别页面，而不是全部的页面，这也是搜索引擎对网页的快照更新时间变短的原因。<span style=\"box-sizing: border-box; text-indent: 2em;\">比如说经常更新的页面，快照也会经常对其进行爬取，从而可以及时发现新内容与链接，删除不存在的信息，所以站长一定要长期坚持更新网页，才能使搜索引擎爬虫稳定前来抓取。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">2、网站框架设计</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">对于网站的内部框架的设计，是要从很多方面进行的，其中，代码需要尽量简明清晰，代码过多容易导致页面体积过大，影响网络爬虫的抓取速度。<span style=\"box-sizing: border-box; text-indent: 2em;\">在抓取网站时，同时网页flash图片尽量少一些，flash格式的内容影响蜘蛛抓取，对于新网站来说，尽量选用伪静态形式的URL，这样可以让整个网站的页面都容易被抓取。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">在设计时对于锚文本文字要分布合理，不能全写关键词，要适当的增加一些长尾词链接。对于内部链接设计也要通畅，方便权重传递。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">3、网站导航设计</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站面包屑导航是网站设计的时候很多企业都会忽视的一个地方，导航是蜘蛛抓取的关键，如果网站导航不清晰，那么搜索引擎在抓取时就容易迷路，所以导航一定要合理的进行设计。<span style=\"box-sizing: border-box; text-indent: 2em;\">这里顺便提及锚文本建设，站内锚文本有利于网络爬虫发现和抓取更多站内网页，但是如果锚文本太多又容易被看成是刻意的调整，在设计时要把握锚文本的数量。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">4、稳定更新频率</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">除了首页的设计之外，网站还有其他页面。爬虫抓取时不会对网站上的全部网页都建立索引，在它们找到重要的页面之前，可能已经抓取了足够多的网页离开了。<span style=\"box-sizing: border-box; text-indent: 2em;\">所以要保持保持一定的更新频率，更新频繁的页面能轻易被抓取，因此能自动抓取数量很多的页面，同时我们要注意网站层级的设计，不能太多，不然也不利于网站抓取。</span></p><p><br/></p>', '', '如何提高搜索引擎抓取频率', '存在的信息，所以站长一定要长期坚持更新网页，才能使搜索引擎爬虫稳定前来抓取。2、网站框架设计对于网站的内部框架的设计，是要从很多方面进行的，其中，代码需要尽量简明清晰，代码过多容易导致页面体积过大，影响网络爬虫的抓取速度。在抓取网站时，同时网页flash图片尽量少一些，flash格式的内容影响蜘蛛抓取，对于新网站来说，尽量选用伪静态形式的URL，这样可以让整个网站的页面都容易被抓取。在设计时对于锚', 0, 1, '1', '1', 2, 0, 0, 1, 0, '2020-09-04 10:42:09', '0000-00-00 00:00:00', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (14, '外链对权重的影响', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">导入链接对于网站优化来说也是非常重要的一个过程，能够间接影响网站在搜索引擎中的权重。目前我们常用的链接分为：锚文本链接、超链接、纯文本链接和图片链接。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">首先是在大网站下的外链建设，大网站外<span style=\"box-sizing: border-box; text-indent: 2em;\">链建设对于站长朋友们来说是非常重要的，因为大网站的权重传递效果非常强，而且还能够为内容带来更多的转载，从而在权重传递上往往能够实现以一敌百的作用，比如在大站上的投稿，就是一个不错的方法。另外还有在各大门户网站上比如网易、新浪等相关频道上投稿或者花钱投稿。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">其实在这些大型网站上投稿或者发布外链并不简单，看起来只要花钱或者聘请枪手就能够实现，但是如果不注重外链的布局就很难提升优化效果，比如媒体投稿，在结束后面添加的文本链接应该是网站的首页链接，这么做的好处相对于这个网站的站长在媒体上投稿，具有一定的相关性，如果你留下的外链是一个销售成人用品的页面，这种相关性就会变得极其脆弱，就很难实现权重的导入。对于其他大型门户网站上的外链建设同样如此，一定要注重外链和结果页面的相关性。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">然后就是合理布局长尾关键词外链，根据二八原则，现代网站的盈利往往有八成都是来自于长尾关键词，也就是说长尾关键词已经成为了网站盈利的核心，因此在外链建设上加强对长尾关键词的锚文本外链是有效提升长尾关键词权重和排名的关键性方法，对此对于重要的长尾关键词要建设相应的栏目页，然后外链的来源就应该选择以这些长尾关键词做的栏目页。当然外链的载体内容一定要和栏目页有一定的相关性，否则效果不会很明显。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">最后就是注重网站内容页的权重导入，这个部分同样十分关键，而且对于很多中小网站来说，这种内容页的权重导入不仅仅能够有效提升内容页在搜索引擎里面的排名，更为关键的是还能够有效提升这些内容页的导流作用，因为当人们进入这些内容页之后，就不可避免的会点击这些内容页的扩展链接，从而直接进入这个网站，从而为进一步获得忠诚用户提供了可能。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">那么在建设内容页的外链建设时，一定要规避一个问题，就是内容页作为外链建设的载体，也就是在其他网站上发布的外链内容和外链导入的内容是一模一样的，这对于用户来说显然没有参考性，而是内容页有着一定的区别，或者是对外链上的内容有着更好的补充，就好比百度词条上各种扩展阅读和相关词条的锚文本链接一样，这样能够让用户获得更好的知识，同时也是促进权重的合理导入。</p><p><br/></p>', '', '外链对权重的影响', '朋友们来说是非常重要的，因为大网站的权重传递效果非常强，而且还能够为内容带来更多的转载，从而在权重传递上往往能够实现以一敌百的作用，比如在大站上的投稿，就是一个不错的方法。另外还有在各大门户网站上比如网易、新浪等相关频道上投稿或者花钱投稿。其实在这些大型网站上投稿或者发布外链并不简单，看起来只要花钱或者聘请枪手就能够实现，但是如果不注重外链的布局就很难提升优化效果，比如媒体投稿，在结束后面添加的文', 0, 1, '1', '1', 15, 0, 0, 1, 0, '2020-09-04 10:42:43', '0000-00-00 00:00:00', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (15, '企业网站模板选择与SEO优化技巧', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200904/1599197067_8d777afe26a587e89969.jpg', '', '<p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box; text-indent: 2em;\">一个营销型的网站，是可以通过页面的展示就达到营销的目的，而在这其中，网站模板的选择就显得非常重要。选择一个好的模板，可以帮助企业网站提升公司的信任度、提升流量的转化率。此外，选择合适的模板，还可以减少后期SEO的工作量，达到快速建站的目的。下面，我来介绍一下我选择网站模板的技巧。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"box-sizing: border-box; text-indent: 2em;\"></span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box;\">网站模板的优化主要还是取决于网站内容的规模。如果网站内容非常丰富，网站模板必然也会比较复杂。反之，如果只是普通的企业网站。那么网站模板的优化就会简单很多。</span></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box; text-indent: 2em;\">网站模板的SEO不是网站模板代码的优化，可以说包含了代码优化，但不是就等于是代码优化。这个是需要区分的。网站模板的SEO可以说更多是设计层面的事情。</span></p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">一、网站模板是内容优化框架</strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">一个网站的模板要怎么设计，如何做布局。主要是基于对网站内容的了解，是网站内容整体优化策略的一个部署。更简单一句话就是为内容构建一个更加符合SEO、符合搜索引擎算法的运行框架。这个框架有什么作用，实现三点目标：</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">1.建立一个利于权重传递的内部链接体系，让重要页面能够均衡获得站内权重的支持，使网站有可能能够提升长尾词效果。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2.从整体加快站内新页面被搜索引擎收录的速度。当然加快收录有时候不仅仅只是站内因素的影响，收录机制有时候会影响搜索引擎整体策略放宽也会加速新页面的收录。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">3.通过模板布局设计让重点类型页面能够更快的获得搜索引擎排名。</p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">二、<span style=\"box-sizing: border-box; text-indent: 2em;\">网站模板的优化应该主要是为了达到这三个目的。</span></strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">那么重复或相似度高的模板有什么影响？</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">重复或者相似度高的模板对网站的SEO还是有蛮大的影响，特别是在同一主题方向的网站都用同一套模板的话，在以前甚至可能会被搜索引擎误判为是作弊。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">然后就算是到了现在阶段肯定也还是有影响。这种影响会增加搜索引擎算法对网站的判断成本，比如搜索引擎需要去判断两个或者多个网站之间是否存在作弊问题。这都会影响主站点在搜索引擎上的搜索表现时间。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">所以要尽量避免主题一样，再使用一样的模板。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><br/></p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">三、网站模板基础优化布局</strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">说了这么多，我们说说如何通过网站模板做基础的SEO布局，来提升网站模板的搜索引友好性。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站首页要给重点页面增加链接，看重点类型页面的数量，如果页面比较多，那就需要增加一个内容模块来放置这些页面链接。如果页面不多的话，也不重点单独增加一个内容模块。但是这些页面在首页的链接区域，最好尽可能靠前。首页的作用重要性非常高。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">其次是栏目页，专题页。这两类页面都是有非常高站内权重的页面。同样的，重要的页面也应该在这两类页面上有链接。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">但最好是能够依据相关性做到个性化的给重要页面布置链接，不要增加一块内容区域，然后就把页面全部丢进去。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">每天更新的内容。关于这类的内容的调用最好能够同时出现在首页，所属栏目页，最终文章页等多个相关类型页面上。因为能够出现在更多页面上的话，能够加快这些页面的收录。</p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">四、先规划再找模板</strong><br style=\"box-sizing: border-box;\"/></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">我们首先要思考自己想要什么样的企业网站模板，做一个规划，这样我们去找模板的时候，才会有目的性的、更快的找到合我们公司的网站模板，避免盲目乱撞，看到这个模板觉得好，那个也不错，最后还是没能找到合适的模板。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">1、从网站定位出发，选择网站模板之前应该想思考：我的网站定位是什么？</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站的定位决定了我们网站的整体风格，人都是“外貌协会”的，外观是网站体验的一部分，虽然外观对排名不能起到决定性的作用，但良好的视觉体验确实能给访客留下一个好印象，对增加网页停留时间、点击率和回访率都是很有利的。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">可以通过一些学习掌握一些基本的视觉认知知识，比如同一页面中色彩数量最好不要超过3个，这有利于我们分辨网站源码模版的优劣。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">对于IT类型的网站，可能绿色、蓝色更能突出科技、创新；对于女性类网站，鲜艳、明亮的色彩更能符合女性审美，并且能带来一定的视觉冲击；而对于母婴类的网站，可能更多的是温暖、温馨的色彩元素。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">2、从公司业务出发，怎么更好地展示公司业务内容？</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">通常在网页上，展示内容的方式有几种：标题（列表）、图片（列表）、图片+标题，这时我们要根据公司的业务产品出发，想想什么样的展现形式能够更好的把信息传递给用户。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">比如，装修网站通常都是图片列表展示，让用户能够看到装修的效果图；装修的知识以文章标题列表展示，能够显示更多的内容。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">3、从用户体验出发，用户来到网站，怎么引导用户浏览以致达到成交？</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网页的布局常见的有两栏布局（1:3或者3:1，常见的网站都是这样的布局），三栏布局（比如 今日头条）。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">那么，我们自己的网站需要怎样布局呢？这是一个在寻找模板之前需要想好的问题。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">或许你不用想的过于详细，但是起码心里得有个谱，也可以用Excel表格规划一下网站首页、栏目页、内容页的大致布局框架，或者用更专业的设计工具来设计网站的页面布局。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><br/></p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">五、网站模板符合SEO标准</strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">（1）网站结构</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站结构包括页面板块结构和内链结构，不同类型的网站对网站模板有着不同的需求，首先应当明确自己网站的结构：横向的、纵向的；目录深度、栏目之间是否有交，等等。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">内链结构就是看网站首页到栏目页，首页到内页、栏目页到文章页等的结构。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">（2）代码精简、规范化</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">目前主流的网站都是PHP开发，前端页面代码都是div+css构成，静态的HTML的页面代码更有利于搜索引擎蜘蛛的抓取，提高收录，如果是动态的网站，相对来说就差一些。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">规范的代码能很好地保留现有强大的SEO优化策略，并且有利于我们对网站源码模板进行改进和二次开发。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">（3）自定义网站tdk、链接</strong></p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">网站的TDK和URL链接是SEO优化当中很重要的设置，设置好的TDK有利于网站的关键词排名，而网站的URL链接会影响搜索引擎蜘蛛的抓取。所以，选择网站模板前先要选择好网站程序。</p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">六、差异化、个性化的网站功能需求</strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">在选择模板的时候，我们可能对网站有些功能上的需求，而且搜索引擎比较喜欢原创性的东西，不光是内容，模板也是。对于不同的网站应用，夹杂着每个站长对这项应用或者服务的理解和规划，而网站中个性化的功能或设置正是能体现这个网站特色或更具备竞争力的地方。所以，在网站模板的选择过程中，同样应该充分的考虑这些个性化的需求。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">比如，回到顶部；</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">商城类的模板可能需要在商品图片中加入图片放大特效，或者图片旋转特效；</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">IT类的网站可能需要内容页支持代码的运行和编辑。</p><h4 style=\"box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">七、网站源码模板测试</strong></h4><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">最后，网站模板下载下来之后，还需要对模板进行测试。我们可以先在本地建站，在自己电脑上安装模板，进行各项测试：</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">1、安全性测试，查看模板是否恶意代码</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">2、完整性测试：看源码是否缺少文件，如js、css等</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">3、前端测试：展示效果、功能等</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">4、功能测试：网站的各项功能是否完善</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em;\">5、后台使用测试：熟悉后台的各项功能</p><p><br/></p>', '', '企业网站模板选择与SEO优化技巧', '后期SEO的工作量，达到快速建站的目的。下面，我来介绍一下我选择网站模板的技巧。网站模板的优化主要还是取决于网站内容的规模。如果网站内容非常丰富，网站模板必然也会比较复杂。反之，如果只是普通的企业网站。那么网站模板的优化就会简单很多。网站模板的SEO不是网站模板代码的优化，可以说包含了代码优化，但不是就等于是代码优化。这个是需要区分的。网站模板的SEO可以说更多是设计层面的事情。一、网站模板是内容', 0, 0, '1', '', 12, 0, 0, 1, 1, '2020-09-04 10:43:57', '2020-09-04 13:24:28', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (16, '网页文字排版设计的10个小技巧', 1, 2, 2, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200904/1599197055_bf3e398796a0804489c1.jpg', '', '<p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">沟通在设计中起着至关重要的作用。这是建立用户和网站之间的明确联系，并帮助你的用户实现目标的关键。当我们谈论网页设计环境中的沟通时，这通常是指文本。排版在这个过程中起着至关重要的作用：网络中超过95％的信息是以文字的形式出现。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187773519462.jpg\" title=\"1599187773519462.jpg\" alt=\"1.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">好的排版提高了用户的阅读积极性性，反之，差的排版会降低用户的阅读积极性。 正如Oliver Reichenstein在他的文章《Web Design is 95% Typography》中所说：</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box; text-indent: 2em;\">优化排版就是优化可读性，可访问性，可用性！让整体的图形平衡。</span></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">换句话说：优化你的排版就是优化你的用户界面。在这篇文章中，我将提供一组规则，帮助你提高文本内容的可读性。</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">一、减少不同类型字体的使用</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">使用超过3种不同的字体让网站看起来没有结构且不专业。记住，太多的尺寸类型和风格也可能破坏任何布局。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187796415944.jpg\" title=\"1599187796415944.jpg\" alt=\"2.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">为了防止这种情况，尝试将字体数量限制在最小限度</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">一般来说，将字体数量限制在最小限度（两个很充足，通常一个就足够了），并粘贴相同的字体到整个网站。如果使用多个字体，请确保字体系基于字符宽度互相补充。以下面的字体组合为例。Georgia和Verdana（左）的组合具有相似的价值，配对的很和谐。比较与Baskerville和Impact（右）的配对，其中大大加重的Impact使与其对应的衬线字体没有光彩。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187802578013.jpg\" title=\"1599187802578013.jpg\" alt=\"3.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">确保字体系基于字符宽度互相补充</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">二、使用标准字体</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">字体的嵌入服务（如Google Web Fonts或Typekit)）可以为你的设计提供新鲜的和意想不到的许多有趣的字体。它们也非常容易使用。以Google为例：</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; text-indent: 0em;\">选择任何字体，如Open Sans</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; text-indent: 0em;\">在HTML文档的&lt;Head&gt;中生成代码并粘贴。</p><p style=\"box-sizing: border-box; overflow-wrap: break-word; text-indent: 0em;\">完成！</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">实际上，这种方法有一个很严重的问题：因为用户更熟悉标准字体，因此可以更快地读取它们。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">除非你的网站对于自定义字体（如对品牌宣传或创建沉浸式体验）非常有吸引力，否则通常最好使用系统字体。最安全的方法是用一个系统的字体：Arial，Calibri，Trebuchet等。记住，好的排版可以吸引读者到内容中去，而不是排版本身。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187808953327.jpg\" title=\"1599187808953327.jpg\" alt=\"4.jpg\"/></p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">三、 限制行的长度</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">每行拥有适当的字符数量是让文本具有可读性的关键。它不是你的设计，决定你的文本的宽度，它应该是一个可读性的问题。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">考虑Baymard Institute关于可读性和行的长度的建议：</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\"><span style=\"box-sizing: border-box; text-indent: 2em;\">“如果你想有一个好的阅读体验，应该每行约60个字符。每行拥有适当的字符数量是让你的文本具有可读性的关键。”</span></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187814487987.jpg\" title=\"1599187814487987.jpg\" alt=\"5.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: center;\">图片来源：Material Design</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">如果行太短，视线必须经常返回，这就会打破读者的节奏。如果一行文字太长，用户的视线将很难专注于文本。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">对于移动设备，应该每行30-40个字符。以下是在移动设备上查看的两个网站的示例。第一个是使用每行50-75个字符，而第二个使是用最佳的30-40个字符。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187821418252.jpg\" title=\"1599187821418252.jpg\" alt=\"6.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: center;\">图片来源：Usertesting</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">在网页设计中，可以通过使用em或像素限制文本的宽度来实现每行最佳数量的字符。</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">四、选择一个能在各种尺寸中工作的字体</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">用户会从具有不同屏幕尺寸和分辨率的设备访问你的网站。大多数用户界面需要各种大小的文本元素（按钮复制，字段标签，章节标题等）。选择一个能够在多种尺寸和重量上运行良好的字体以保持每个尺寸的可读性和可用性是非常重要。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187826491535.jpg\" title=\"1599187826491535.jpg\" alt=\"7.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">Google的Roboto字体</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">确保你说选择的字体在较小的屏幕上清晰可辨！尝试避免使用草书的字体，例如Vivaldi（在下面的示例中）：虽然它们很漂亮，但它们很难阅读。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187832282338.jpg\" title=\"1599187832282338.jpg\" alt=\"8.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">Vivaldi字体很难以在小屏幕上阅读</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">五. 使用可区分字母的字体</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">许多字体让相似的字形很容易混淆，特别是与“i”和“L”（如下图所示）以及在字母间距较小的空间中，例如当“r”和“n”看起来像“M”。因此，在选择你的排版时，请务必在不同的文本环境中检查你的排版，以确保不会为用户造成问题。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187837681015.jpg\" title=\"1599187837681015.jpg\" alt=\"9.jpg\"/></p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">六. 避免所有的大写</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">所有大写字母：意思是文本中的所有大写字母，在不涉及阅读的上下文中很好（如首字母缩略词或标识），但是当你的信息涉及阅读时，不要强制用户阅读所有大写文字。正如Miles Tinker所说，在他的具有里程碑意义的作品中，可读性的印刷，全部大写印刷与小写排版相比，大大地延缓了用户的阅读速度。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187842787572.jpg\" title=\"1599187842787572.jpg\" alt=\"10.jpg\"/></p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">七. 行间距的重要性</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">在排版中，我们有一个特殊术语，用于两行文本之间的间距（或行高）。通过增加行高，可以增加文本行之间的垂直空白空间，通常提高可读性以换取屏幕空间。作为一个规则，行高应该是字符高度的30％，以提高可读性。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187848952781.jpg\" title=\"1599187848952781.jpg\" alt=\"11.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">好的间距有助于可读性。图片来源：Microsoft</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">正如Dmitry Fadeyev所指出的那样，正确地使用段落之间的空白已被证明可以将理解提高20％。使用空白的技能在于为用户提供可消化量的内容，然后剥离无关紧要的细节。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187853383301.jpg\" title=\"1599187853383301.jpg\" alt=\"12.jpg\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">左：几乎重叠的文字。右：良好的间距有助于可读性。图片来源： Apple</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">八. 确保你有足够的颜色对比度</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">不要在文本和背景中使用相同或相似的颜色。文本越明显，用户就能更快地扫描和阅读它。 W3C建议对身体文字和图像文字的对比度如下：</p><ol class=\" list-paddingleft-2\" style=\"margin: 20px 30px; width: 1325.25px; padding: 0px; white-space: normal; box-sizing: border-box; -webkit-tap-highlight-color: transparent; list-style-position: outside; list-style-image: initial; border: 0px; color: rgb(34, 34, 34); font-family: &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, &quot;Helvetica Neue&quot;, Arial, sans-serif; background-color: rgb(255, 255, 255);\"><li><p style=\"box-sizing: border-box; overflow-wrap: break-word; text-indent: 0em;\">与其背景相比，小写文字的对比度应至少为4.5：1。</p></li><li><p style=\"box-sizing: border-box; overflow-wrap: break-word; text-indent: 0em;\">大文字（14pt/ 常规18pt及以上）的背景对比度应至少为3：1。</p></li></ol><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">这些文本行不符合颜色对比度的建议，难以根据背景颜色进行阅读。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">这些文本行符合颜色对比度建议，易于阅读背景颜色。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">一旦你选择了颜色，必须要在大多数设备上与真实用户进行测试。如果测试显示阅读副本有问题，那就可以确定你的用户具有完全相同的问题。</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">九. 避免文本为红色或绿色</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">色盲是一种常见的情况，特别是在男性中（8％的男性是色盲），建议使用除这些颜色以外的其他颜色来区分重要信息。也避免单独使用红色和绿色来传达信息，因为红色和绿色色盲是最常见的色盲形式。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255);\"><br style=\"box-sizing: border-box;\"/></p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">十. 避免使用闪烁的文字</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">闪烁的内容可能会引发敏感个体的癫痫发作。它不仅可以引起癫痫发作，而且对于一般用户来说，这是令人讨厌且使人分心。</p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-align: center;\"><img src=\"http://localhost/static/upload/20200904/1599187863112095.gif\" title=\"1599187863112095.gif\" alt=\"13.gif\"/></p><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: center;\">避免闪烁文字！</p><h4 style=\"white-space: normal; box-sizing: border-box; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-weight: 500; line-height: 1.1; color: rgb(51, 51, 51); margin-top: 10px; margin-bottom: 10px; background-color: rgb(255, 255, 255); text-indent: 2em;\"><strong style=\"box-sizing: border-box;\">总结</strong></h4><p style=\"white-space: normal; box-sizing: border-box; overflow-wrap: break-word; color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Segoe UI&quot;, Roboto, &quot;Helvetica Neue&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; background-color: rgb(255, 255, 255); text-indent: 2em;\">排版是一件重要的事情。做出正确的排版选择可以让你的网站看上去更优雅。另一方面，糟糕的排版选择会让人分心，往往会引起对排版的注意。让排版具有可读性，可理解性和清晰度是至关重要。</p><p><br/></p>', '', '网页文字排版设计的10个小技巧', '信息是以文字的形式出现。好的排版提高了用户的阅读积极性性，反之，差的排版会降低用户的阅读积极性。 正如Oliver Reichenstein在他的文章《Web Design is 95% Typography》中所说：优化排版就是优化可读性，可访问性，可用性！让整体的图形平衡。换句话说：优化你的排版就是优化你的用户界面。在这篇文章中，我将提供一组规则，帮助你提高文本内容的可读性。一、减少不同类型字', 0, 0, '0', '0', 52, 0, 0, 1, 1, '2020-09-04 10:46:47', '2020-09-04 15:30:31', '', '', 1, 0);
INSERT INTO `{pre}content` VALUES (17, '服务案例演示1', 1, 8, 8, '', '', '', 'admin', 'jellycms', '', '0000-00-00 00:00:00', '/static/upload/20200905/1599313831_070f5b764875158b6310.jpg', '', '<p>服务案例演示1</p>', '', '1', '', 0, 0, '', '', 1, 0, 0, 1, 1, '2020-09-04 13:23:56', '2020-09-05 21:51:09', '', '', 1, 0);
