INSERT INTO `{pre}content` VALUES (19, '服务案例演示3', 1, 8, 8, '', '', '', '', '', '', '0000-00-00 00:00:00', '/static/upload/20200905/1599313922_ce113a23193ffac1331f.jpg', '', '<p>服务案例演示3</p>', '', '服务案例演示3', '', 0, 0, '', '', 1, 0, 0, 1, 0, '2020-09-05 21:52:04', '0000-00-00 00:00:00', '', '', 1, 0);
