-- ----------------------------
-- Table structure for jelly_form_comment
-- ----------------------------
DROP TABLE IF EXISTS `{pre}form_comment`;
CREATE TABLE `{pre}form_comment`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content_id` int(11) NOT NULL COMMENT '内容ID',
  `content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '评论内容',
  `pid` int(11) NOT NULL DEFAULT 0 COMMENT '父ID',
  `create_time` datetime(0) NOT NULL,
  `create_user` int(11) NOT NULL,
  `ip` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `icon` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `nickname` varchar(30) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '昵称',
  `status` tinyint(1) NOT NULL COMMENT '0：前台不显示；1：前台展示；3：审核中  与form表type关联',
  `deleted` tinyint(1) NOT NULL DEFAULT 0,
  `area_id` int(11) NOT NULL COMMENT '区域ID',
  `checked` int(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 33 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for jelly_form_field
-- ----------------------------
DROP TABLE IF EXISTS `{pre}form_field`;
CREATE TABLE `{pre}form_field`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `form_id` int(11) NOT NULL,
  `name` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `length` bigint(20) NOT NULL,
  `required` tinyint(1) NOT NULL,
  `description` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `sorting` bigint(20) NOT NULL,
  `create_user` int(11) NOT NULL,
  `update_user` int(11) NOT NULL,
  `create_time` timestamp(0) NOT NULL,
  `update_time` timestamp(0) NOT NULL,
  `deleted` tinyint(1) UNSIGNED ZEROFILL NOT NULL DEFAULT 0,
  `type` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'mysql字段类型',
  `issystem` tinyint(1) UNSIGNED ZEROFILL NOT NULL DEFAULT 0 COMMENT '1:系统内置',
  `display` tinyint(1) UNSIGNED ZEROFILL NOT NULL DEFAULT 1 COMMENT '是否显示在后台列表',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `ay_form_field_sorting`(`sorting`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 26 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

