-- Table structure for jelly_site
-- ----------------------------
DROP TABLE IF EXISTS `{pre}site`;
CREATE TABLE `{pre}site`  (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `area_id` int(11) NOT NULL COMMENT '区域ID',
  `title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站标题',
  `subtitle` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站子标题',
  `domain` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站域名',
  `logo` varchar(60) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站LOGO',
  `keywords` varchar(300) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站关键词',
  `description` varchar(300) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站描述',
  `icp` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站域名备案号',
  `statistics` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站统计信息',
  `copyright` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站版权',
  `qrcode` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '网站二维码',
  `service_code` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '服务器号二维码',
  `subscribe_code` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '订阅号二维码',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 3 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of jelly_site
-- ----------------------------
INSERT INTO `{pre}site` VALUES (1, 1, 'JellyCMS', '开源免费PHP企业网站内容管理系统', 'https://www.jellycms.cn', '/static/upload/20200824/1598266703_31cfb380618235447fe4.png', 'phpcms,免费cms,php开源cms,php企业cms,php建站cms', 'JellyCMS是北京国栋科技有限公司开发的企业信息化解决方案系统之一，永久开源免费的PHP企业网站开发建设管理系统，是一套高效、简洁、安全、易用的遵循MIT协议的免费PHP CMS源码网站开发建设解决方案。', '京ICP备19013673号', '', 'CopyRight 2015-2020 All Right Reserved', '/static/upload/20200819/1597822100_12968a3ea78c50d7f90d.png', '/static/upload/20200819/1597822100_12968a3ea78c50d7f90d.png', '/static/upload/20200819/1597822100_12968a3ea78c50d7f90d.png');

